    public void tearDown() throws Exception {
        testFile.delete();
    }

    @Test
    public void createPDFFromImageString() throws IOException, DocumentException {
        testFile = pdfService.createPDFFromImageString(base64_str);
        Assert.assertTrue(testFile.exists());
    }
